import React from 'react';
import axios from "axios";
import TextField from '@material-ui/core/TextField';
import Button from '@material-ui/core/Button';

class Search extends Component{
    state={
        isDisplay:false,
        searchData:[]
    }

    Search=()=>{
        axios.get("https://swapi.co/api/people/?search="+ this.state.searchInput)
        .then((res)=> { 
          console.log("res: "+ res.data.results[0].name)
          this.setState({searchData: res.data.results})});
      }

    render(){
    return(<div>
            <TextField onChange={(e)=>{this.setState({searchInput: e.target.value})}} id="outlined-basic" label="Outlined" variant="outlined" />
            <Button onClick={this.Search} variant="contained" color="primary">
                Submit
            </Button>

            {/* {this.state.isDisplay} */}

        </div>
    )
    }
}


export {Search}
