            <Tab style={tabCss} onClick={()=>this.combineFunction(1, this.planet)} label="View Planes" />
            <Tab style={tabCss} onClick={()=>this.combineFunction(2, this.starship)} label="View Starships" />  
        </TabContainer>        
        </Nav> */}

