          <Element name="secondInsideContainer" style={{

          }}>
           <section ref={(section) => { this.planet = section; }}>
            <Planet />
          </section>
          </Element>
          <Element name="secondInsideContainer" style={{

          }}>
            <section ref={(section) => { this.starship = section; }}>
            <Starship />
          </section>
          </Element>
        </Element>

      </MainWrapper>
    </Wrapper>
  );
  }
}

export default App;

