import React, {Component} from 'react';
import axios from "axios";
import TextField from '@material-ui/core/TextField';
import Button from '@material-ui/core/Button';
import styled from "styled-components";
import {PersonCards} from "../Components/Cards/PersonCards";

class Search extends Component{
    state={
        isDisplay:false,
        searchData:[]
    }

    Search=()=>{
        axios.get("https://swapi.co/api/people/?search="+ this.state.searchInput)
        .then((res)=> { 
          console.log("res: "+ res.data.results[0])
          this.setState({searchData: res.data.results, isDisplay: true})});
      }


    render(){    

    const {searchData} = this.state;

    return(<div>
            <Title>Search a person through our database</Title>
            <SearchContainer>
                <TextField onChange={(e)=>{this.setState({searchInput: e.target.value})}} id="outlined-basic" label="Outlined" variant="outlined" />
                <Button onClick={this.Search} variant="contained" color="primary">
                    Submit
                </Button>
            </SearchContainer>

    {this.state.isDisplay ? <PersonCards name={searchData[0].name} height={height} mass={mass} hairColor={hairColor}/> : null}

        </div>
    )
    }
}
