import React, {Component} from 'react';
import './App.css';
import Headroom from "react-headroom";
import styled from "styled-components";
import scrollToComponent from 'react-scroll-to-component';
import {Landing} from "./Sections/Landing";
import {People} from "./Sections/People";
import {Planet} from "./Sections/Planet";
import {Starship} from "./Sections/Starship";
import Tabs from '@material-ui/core/Tabs';
import Tab from '@material-ui/core/Tab';
import bgImg from "./Images/background_images.jpg";

class App extends Component {

  state = {
    currentTab: 0,

    searchInput: "",
    originalCard:[]
  }

  handleTabChange=(loc)=>{
    this.setState({currentTab : loc});
  }

  combineFunction=(tab, loc)=>{
    this.setState({currentTab : tab})
    scrollToComponent(loc, { offset: 0, align: 'top', duration: 1500, ease:'inCirc'})
  }


  render(){
  return (
    <Wrapper >

        <Nav>
          <TabContainer
          value={this.state.currentTab}
          aria-label="navigation"
          centered
        >
            <Tab onClick={()=>this.combineFunction(0, this.people)} label="View people" />
            <Tab onClick={()=>this.combineFunction(1, this.planet)} label="View Planes" />
            <Tab onClick={()=>this.combineFunction(2, this.starship)} label="View Starships" />

        </TabContainer>        
        </Nav>
    <MainWrapper>
        <Landing />

        <section ref={(section) => { this.people = section; }}>
          <People />
        </section>
        <section ref={(section) => { this.planet = section; }}>
          <Planet />
        </section>
        <section ref={(section) => { this.starship = section; }}>
          <Starship />
        </section>
      </MainWrapper>
    </Wrapper>
  );
  }
}

export default App;

const Wrapper = styled.div`
  text-align:center;
  background:url(${bgImg}); 
`;

const Nav = styled(Headroom)`
  position:absolute;
  width:100%;
`;

const TabContainer = styled(Tabs)`
background-color: rgba(0,0,0, 0.6);
height: 4em;

.MuiTabs-indicator{
  background-color:blue; 
}
`
const MainWrapper = styled.div`
margin: 0 10%;
`;
