import React, {Component} from 'react';
import axios from "axios";
import TextField from '@material-ui/core/TextField';
import Button from '@material-ui/core/Button';
import styled from "styled-components";

class Search extends Component{
    state={
        isDisplay:false,
        searchData:[]
    }

    Search=()=>{
        axios.get("https://swapi.co/api/people/?search="+ this.state.searchInput)
        .then((res)=> { 
          console.log("res: "+ res.data.results[0])
          this.setState({searchData: res.data.results, isDisplay: true})});
      }


    render(){    
    return(<div>
            <Title>Search a person through our database</Title>
            <SearchContainer>
                <TextField onChange={(e)=>{this.setState({searchInput: e.target.value})}} id="outlined-basic" label="Outlined" variant="outlined" />
                <Button onClick={this.Search} variant="contained" color="primary">
                    Submit
                </Button>
            </SearchContainer>

    {this.state.isDisplay ? <p>name: {searchData}</p> : null}

        </div>
    )
    }
}


export {Search}

const Title = styled.div`

`;

const SearchContainer = styled.div`
display:flex;
flex-direction: column;
margin: 5% 10%;
`;
